const pageWidth = document.documentElement.scrollWidth

function chengeContent(content) {
    document.querySelector(".content").innerHTML = content
   
    if(pageWidth<1100){
        document.getElementById("burger").classList.remove("hide")
        document.querySelector(".aside").classList.add("hide")
        document.querySelector(".wrapper").classList.add("mobile")
        document.querySelector(".content").classList.remove("hide")
        document.querySelector(".content")?.classList.add("content_mobile")
        document.querySelector(".content").classList.remove("content")
        document.querySelector(".inner_content_body")?.classList.add("inner_content_body_mobile")
        console.log(pageWidth);     
        
        document.querySelectorAll("img:not(#burger)").forEach(img=>{
            console.log(img)
            
            img.width = pageWidth-30
        })
    }
    

}

document.addEventListener("click", function (e) {
    
    if (e.target.classList.contains("aside-item")) {
        document.querySelector(".clicked")?.classList.remove("clicked")
        e.target.classList.add("clicked")
    }
    

});
document.addEventListener("DOMContentLoaded", getParam);

function getParam(){ 
    console.log(pageWidth);
    document.getElementById("burger").classList.add("hide")
    if(pageWidth<1100){
        document.querySelector(".content").classList.add("hide")
        document.querySelector(".wrapper").classList.add("mobile")
    }
    
}


document.getElementById("burger").addEventListener("click", showMenu)
function showMenu(){
    document.querySelector(".content_mobile").classList.add("content")
    document.querySelector(".content").classList.add("hide")
    document.querySelector(".content_mobile").classList.add("content_mobile")
    document.querySelector(".clicked").classList.remove("clicked")
    document.querySelector(".aside").classList.remove("hide")
    document.getElementById("burger").classList.add("hide")
}

document.getElementById("id_14").addEventListener('click', showSelfControl)

function showSelfControl(){
    document.querySelectorAll(".short").forEach(el=>{
        el.classList.toggle("hide")
    })
}